create table "cell_in_stock"(	"id"				integer			not null primary key,	"modified"			integer			not null default(strftime('%s', 'now')),	"article_id"		integer			not null references "article"("id"),	"point_of_sale_id"	integer			not null references "point_of_sale"("id"),	"x"					varchar(10)		not null collate UTF8CI,	"y"					varchar(10)		not null collate UTF8CI,	"amount"			integer			not null check ("amount" >= 0));create index "ix_cell_in_stock_parent" on "cell_in_stock" ("article_id", "point_of_sale_id");